import React from 'react';
import './App.css';

class App extends React.Component {

    componentWillUnmount() {
        let dropdown = document.getElementById('dropdown');
        dropdown.length = 0;

        let defaultOption = document.createElement('option');
        defaultOption.text = 'Choose City';

        dropdown.add(defaultOption);
        dropdown.selectedIndex = 0;

        const url = 'https://github.com/vipul018/klaviyo-weather-powered-email/blob/master/public/cities.json';

        fetch(url)
            .then(
                function (response) {
                    if (response.status !== 200) {
                        console.warn('Error downloading teh JSON file. Status code: ' + response.status);
                        return;
                    }

                    response.json().then(function (data) {
                        let option;

                        for (let i = 0; i < data.length; i++) {
                            option = document.createElement('option');
                            option.text = data[i].name;
                            option.value = [data[i].latitude, data[i].longitude];
                            dropdown.add(option);
                        }

                    });
                }
            ).catch(function (err) {
            console.log('Fetch Error! - ', err);
        });
    }

    render() {
        return (
            <div className="form-label-group">
                <div className="dropdown show" id="dropdown">
                    <select className="form-control form-control-lg">
                        <option default>Select Your City</option>
                        <option>Large select</option>
                    </select>
                </div>
            </div>

        );
    }
}

export default App;
