import React from 'react';
import data from './cities';

class App extends React.Component {

    componentDidMount() {
        let dropdown = document.getElementById('dropdown');
        dropdown.length = 0;

        let defaultOption = document.createElement('option');
        defaultOption.text = 'Choose City';

        dropdown.add(defaultOption);
        dropdown.selectedIndex = 0;

        let option;

        for (let i = 0; i < data.length; i++) {
            option = document.createElement('option');
            option.text = data[i].city;
            option.value = [data[i].latitude, data[i].longitude];
            dropdown.add(option);
        }

        console.log(option);
    }

    render() {
        return (
            <div>

                <div className="form-label-group">
                    <div className="dropdown show">
                        <select className="form-control form-control-lg" id="dropdown">
                        </select>
                    </div>
                </div>

                <button className="btn btn-lg btn-primary btn-block" type="submit"> Subscribe</button>
            </div>
        );
    }
}

export default App;
